sysauto_script.script:
// Get today's time and date
var rightnow = new GlideDateTime();

//Query the database for NeedIt records with the Due date field values older than the current time. 
//Only return NeedIt Task records that do not have a State field value of Closed Complete.
var overdueNeedItTasks = new GlideRecord('x_58872_needit_needit_task');

overdueNeedItTasks.addQuery('due_date','<=',rightnow);
overdueNeedItTasks.addQuery('state','!=',3);

overdueNeedItTasks.query();

//Write a log message for each overdue NeedIt Task record
while(overdueNeedItTasks.next()){
	gs.info("Overdue NeedIt Task record = " + overdueNeedItTasks.number);
	gs.eventQueue('x_58872_needit_needit_task',overdueNeedItTasks,overdueNeedItTasks.number,gs.getUserName());
}
